# Add to src/config_helper.py
class ConfigHelper:
    def get_hardcoded_value(self, category: str, key: str, env: str = None) -> str:
        env_config = self._load_environment_config(env or self._detect_environment())
        override_path = f'hardcoded_overrides.{category}.{key}'
        return env_config.get(override_path) or self.get(f'{category}.{key}')

    def _load_environment_config(self, env: str) -> dict:
        env_file = Path(__file__).parent.parent / 'config' / 'environments' / f'{env}.yaml'
        return yaml.safe_load(env_file.open()) if env_file.exists() else {}

    def _detect_environment(self) -> str:
        return os.getenv('AUTOBOT_ENVIRONMENT', 'development')
```

